package alb_TA04;

public class Ej_04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 35;
		int resul = n;
		System.out.println("Valor inicial de N = " + resul);
		resul = resul + 77;
		System.out.println("N + 77 = " + resul);
		resul = resul - 3;
		System.out.println("N - 3 = " + resul);
		resul = resul * 2;
		System.out.println("N * 2 = " + resul);
		
	}

}
